
public class TestIfelse {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i = 5;
        if(i==5){
        	System.out.println("i==5");
        }
        else if(i > 3){
        	System.out.println("i > 3");
        }
        else{
        	System.out.println("else");
        }
	}

}
