
public class TestSwitch {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 3;
		switch(i){
	    case 1:
	    	System.out.println(i);
	    	break;
	    case 2:
	    	System.out.println(i);
	    	break;
	    default:
	    	System.out.println("default");
		}
	    	
	}

}
